<?phpnamespace App\Controllers;use App\Utilities\SessionUtility;class PagesController {    protected $view;    protected $sessionUtility;    public function __construct(View $view, SessionUtility $sessionUtility)     {        $this->view = $view;          $this->sessionUtility = $sessionUtility;         }        public function error()    {               header("HTTP/1.0 404 Not Found");        $errormsg = "Oops! The page you are looking for does not exist.";        if($this->sessionUtility->isLoggedIn()){            $this->view->setHeaderFile("views/userheader.php");        }        return $this->view->show('pages/error', compact('errormsg'));            }        public function contact()    {        return $this->view->show('pages/contact');            }}     ?>