<?phpnamespace App\Models;class BlogComment{    public $commentId = '';    public $commentPostId = '';    public $commentText = '';    public $commentDate = '';    public $commentIsVisible = '';    public $commentName = '';    public function __construct($commentData = [])     {        $this->setData($commentData);    }    public function setData($commentData = [])    {        foreach($commentData as $field => $data) {            if(isset($this->$field)) {                $this->$field =  $data;            }        }        return $this;    }}