<?phpnamespace App\Models;class BlogPost{    public $postId = '';    public $postTitle = '';    public $postDesc = '';    public $postText = '';    public $postDate = '';    public $postReads = '';    public $postLikes = '';    public $postImage = '';    public $postUserId = '';    public $postIsVisible = '';        public function __construct($postData = [])     {        $this->setData($postData);        // $this->postId = $id;        // $this->postTitle = $title;        // $this->postDesc = $desc;        // $this->postText = $text;        // $this->postDate = $date;        // $this->postReads = $reads;        // $this->postLikes = $likes;        // $this->postImage = $image;        // $this->postUserId = $userId;        // $this->postIsVisible = $isVisible;    }    public function setData($postData = [])    {        foreach($postData as $field => $data) {            if(isset($this->$field)) {                $this->$field =  $data;            }        }        return $this;    }}