<?phpnamespace App\Models;class BlogPost{    public $postId;    public $postTitle;    public $postDesc;    public $postText;    public $postDate;    public $postReads;    public $postLikes;    public $postImage;    public $postUserId;    public $postIsVisible;        public function __construct($id=null, $title=null, $desc=null, $text=null, $date=null, $reads=null, $likes=null, $image=null, $userId=null, $isVisible=null)     {        $this->postId = $id;        $this->postTitle = $title;        $this->postDesc = $desc;        $this->postText = $text;        $this->postDate = $date;        $this->postReads = $reads;        $this->postLikes = $likes;        $this->postImage = $image;        $this->postUserId = $userId;        $this->postIsVisible = $isVisible;    }}