<?phpnamespace app\models;use PDO;class Database {    private $conn;     public function __construct($dataSourceName, $databaseUsername, $databasePassword) {      try {            $this->conn = new PDO($dataSourceName,                                   $databaseUsername,                                   $databasePassword,                                   [PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION] );       }catch(PDOException $pe) {            error_log("<br /> Exception occurred ". $pe->getMessage());       }     }    public function getConnection() {            if($this->conn instanceof PDO) {                     return $this->conn;            } else {                      return false;            }    }}