<?phpnamespace BW\controllers;class PostsController extends BaseController {     public function index() {        $blogPostsList = $this->blogPostDatabase->getVisiblePosts();        foreach ($blogPostsList as $singleBlogPost) {            $blogUsersList[$singleBlogPost->postuserid] = $this->blogUserDatabase->getUserById($singleBlogPost->postuserid);        }        $this->view->setData("blogPostsList",$blogPostsList);        $this->view->setData("blogUsersList",$blogUsersList);                $this->view->setContentFile("views/posts/index.php");        $this->view->renderView();    }    public function show() {        if ($_SERVER['REQUEST_METHOD'] == 'POST') {            $result = $this->newcomment();            if (is_array($result)) {                $errorMessages = $result;                $this->view->setData("errorMessages",$errorMessages);            }        }        if (!isset($_GET['id'])) {            $this->error("No post id mentioned");            return;        }        $id = htmlspecialchars($_GET['id']);        $blogPost = $this->blogPostDatabase->getPost($id);        //print_r($bpost);        if (!$blogPost instanceof \BW\tools\blogpost) {            $this->error("Cannot find the post with id $id");            return;        }        $blogUser = $this->blogUserDatabase->getUserById($blogPost->postuserid);        $this->blogPostDatabase->updatePostRead($blogPost->postid);        $blogCommentsList = $this->blogCommentDatabase->getCommentsByPost($blogPost->postid);        $pageTitle = "Welcome to Articles By U --- $blogPost->posttitle";        $this->view->setData("pageTitle",$pageTitle);        $this->view->setData("blogPost",$blogPost);        $this->view->setData("blogUser",$blogUser);                $this->view->setData("blogCommentsList",$blogCommentsList);                        $this->view->setContentFile("views/posts/show.php");        $this->view->renderView();         }    public function error($errorMessage) {                $this->view->setData("errorMessage",$errorMessage);        $this->view->setContentFile("views/posts/error.php");        $this->view->renderView();            }    public function newcomment() {        //echo "new comment";        if (!($_SERVER['REQUEST_METHOD'] == 'POST')) {            return;        }        // so, the following lines get executed only, if the request method is post        $errorMessages = [];        $blogComment = new \BW\tools\blogcomment();        if (empty($_POST['txtcommentname'])) {            $errorMessages[] = "Please enter your name";        } else {            $blogComment->commentname = $this->test_input($_POST['txtcommentname']);        }        if (empty($_POST['txtcommenttext'])) {            $errorMessages[] = "Please enter your comment";        } else {            $blogComment->commenttext = $this->test_input($_POST['txtcommenttext']);        }        if (empty($_POST['txtcommentpostid'])) {            $errorMessages[] = "Please enter your comment post id";        } else {            $blogComment->commentpostid = $this->test_input($_POST['txtcommentpostid']);        }        if (!empty($errorMessages)) {            return $errorMessages;        }        // so , the following lines get executed only if errormsgs is empty, i.e there are no errors        $blogComment->commentdate = time();        $blogComment->commentisvisible = 1;        $result = $this->blogCommentDatabase->addComment($blogComment);        return $result;    }    public function search() {        // echo "<br /> hitting search";        if ($_SERVER['REQUEST_METHOD'] == 'POST') {            $searchtext = '';            if (!empty($_POST['txtsearch'])) {                $searchtext = $this->test_input($_POST['txtsearch']);            }            $blogposts = $this->blogPostDatabase->getPostsByText($searchtext);        }        include("views/header.php");        include("views/posts/search.php");        include("views/footer.php");    }}?>