<?phpnamespace BW\tools;class BlogComment{    public $commentid;    public $commentpostid;    public $commenttext;        public $commentdate;    public $commentisvisible;        public $commentname;          public function __construct($id=null, $postid=null, $text=null, $date=null, $isvisible=null, $name=null) {        $this->commentid = $id;        $this->commentpostid = $postid;        $this->commenttext = $text;        $this->commentdate = $date;        $this->commentisvisible = $isvisible;        $this->commentname = $name;                    }}