<?phpnamespace BW\tools;class blogpost{    public $postid;    public $posttitle;    public $postdesc;        public $posttext;    public $postdate;    public $postreads;    public $postlikes;    public $postimage;    public $postuserid;    public $postisvisible;        public function __construct($id=null, $title=null, $desc=null, $text=null, $date=null, $reads=null, $likes=null, $image=null, $userid=null, $isvisible=null) {        $this->postid = $id;        $this->posttitle = $title;                $this->postdesc = $desc;        $this->posttext = $text;                        $this->postdate = $date;        $this->postreads = $reads;        $this->postlikes = $likes;        $this->postimage = $image;        $this->postuserid = $userid;        $this->postisvisible = $isvisible;    }}