<?phpnamespace BW\tools;class Database {    private $conn;     public function __construct($dataSourceName, $databaseUsername, $databasePassword) {      try {            $this->conn = new \PDO($dataSourceName, $databaseUsername, $databasePassword, array(\PDO::ATTR_ERRMODE => \PDO::ERRMODE_EXCEPTION) );       }catch(\PDOException $pe) {            echo "<br /> Exception occurred ". $pe->getMessage();       }     }    public function getConnection() {            if($this->conn instanceof \PDO) {                     return $this->conn;            } else {                      return false;            }    }}?>