<div class="content"><div class="searchpanel"><form method="post" action="/search">            <fieldset>                          <label for="txtsearch">Search Articles </label>                <input class="searchtext" type="text" id="txtsearch" name="txtsearch" placeholder="Search for Articles" value="<?= isset($searchText) ? $searchText : null ?>" required/>             <input class="searchbutton" type="submit" value="Search" />             </fieldset>   </form></div><div class="panel"><?phpif(!empty($blogPosts)):?>    <h3>Your Search Results :</h3>    <ul>    <?php    foreach($blogPosts as $singlePost):        ?>            <li>                    <?= $singlePost->postTitle ?>                <a href="/showpost?id=<?= $singlePost->postId ?>">Read More </a>        </li>    <?php    endforeach;    ?>        </ul><?phpelse:?>       <h3><?= isset($searchText) ? "Your Search Returned No Results." : null ?></h3><?php   endif;?></div>    </div>