<?phpnamespace BW;?><div class="content"><div class="searchpanel"><form method="post" action="/search">            <fieldset>                          <label for="txtsearch">Search Articles </label>                <input class="searchtext" type="text" id="txtsearch" name="txtsearch" placeholder="Search for Articles" value="<?= isset($searchtext) ? $searchtext : null ?>" required/>             <input class="searchbutton" type="submit" value="Search" />             </fieldset>   </form></div><div class="panel"><?phpif(!empty($blogposts)):?>    <h3>Your Search Results :</h3>    <ul>    <?php    foreach($blogposts as $singlepost):        ?>            <li>                    <?= $singlepost->posttitle ?>                <a href="/showpost?id=<?= $singlepost->postid ?>">Read More </a>        </li>    <?php    endforeach;    ?>        </ul><?phpelse:?>       <h3><?= isset($searchtext) ? "Your Search Returned No Results." : null ?></h3><?php   endif;?></div>    </div>