<div class="content"> <div class="panel">  <h1><?= $blogPost->postTitle; ?> </h1>  <img class="showpost" src="<?= $blogPost->postImage; ?>"/>  <p class="postedby">Posted by <em><?= $blogUser->userFirstName; ?> <?= $blogUser->userLastName; ?></em> on <?= $blogPost->postDate; ?> </p>  <p class="posttext"><?= nl2br($blogPost->postText); ?></p></div><div class="panel">      <h2><?= (count($blogCommentsList)== 0) ? "No " : count($blogCommentsList) ?> Comment(s)</h2>      <?php      foreach($blogCommentsList as $singleBlogComment):        ?>      <h4 class="commentnameheading"><?= $singleBlogComment->commentName ?> says on</h4>      <small><em><?= date('l jS F, Y \a\t g:i a',$singleBlogComment->commentDate) ?></em></small>      <p><?= $singleBlogComment->commentText; ?></p>      <?php       endforeach;      ?></div><div class="panel">    <h2> Leave Your Comment</h2>    <form method="post" action="/showpost?id=<?= $blogPost->postId ?>">          <?php          if(isset($errorMessages)):            echo '<ul class="errorslist">';          foreach($errorMessages as $singleErrorMessage):            ?>          <li><?= $singleErrorMessage?></li>          <?php          endforeach;          echo "</ul>";          echo "<script>window.scrollTo(0,document.body.scrollHeight);</script>";          endif;          ?>          <p>          <input type="text" name="commentName" class="commentinput" placeholder="Your Name"           value = "<?= isset($blogComment->commentName) ? $blogComment->commentName : null ?>"/>          </p>          <p>          <textarea name="commentText"  class="commenttextarea" placeholder="Your Comment"><?= isset($blogComment->commentText) ? $blogComment->commentText : null ?></textarea>           </p>           <p>          <input type="hidden" name="commentPostId" value ="<?= $blogPost->postId?>" />          <input type="submit" value="Submit Comment" />          </p>    </form></div></div>